class Solution {
public:
    int addMinimum(string word) {
        //        //
        //  aaa a bc a bc a bc -> 6
        //  bbb a b c   a b c   a b c --> 6
        //  ccc ab c  ab c ab c--> 6
        // 
      int n = word.size();
        string s = "";
      int i = 0,j=0 , cnt = 0;
      while(i<n)
      {
        if(word[i] == s[j] ) {
          i++;
          j++;
          continue;
        }
        else{
          j++;
          cnt++;
        }
      }
      if(word[n-1] != 'c')
      {
        cnt += 'c' - word[n-1];
      }
     return cnt;
    }
};